import Offcanvas from "../offcanvas/Offcanvas";
import Header from "../header/Header";
import Footer from "../footer/Footer";

export default function Login() {
    return (
        <>          
            <section id="login" className="container">
                <article className="narrow">
                    <header className="pad-med">
                        <h1>Login</h1>
                    </header>
                    <form id="login-form" className="main-form pad-large">
                        <div className="error">Error message.</div>
                        <label>E-mail: <input type="text" name="email" placeholder="Enter your email"/></label>
                        <label>Password: <input type="password" name="password" placeholder="Enter your password"/></label>
                        <input class="action cta" type="submit" value="Sign In"/>
                    </form>
                    <footer class="pad-small">Don't have an account? <a href="/register" class="invert">Sign up here</a>
                    </footer>
                </article>
            </section>
        </>
    );
}
