import { useState } from "react";
import Product from "./Product";

export default function ProductList({products}){
    console.log(products);
    const [isActive, setIsActive] = useState({
        bestActive: false,
        newActive: false, 
        hotActive: false,
    });
    const handleClick = (e) => {
        if (e.target.tagName === 'LI' && e.target.value === "Best Sellers") {
            console.log("Best Sellers")
            setIsActive({bestActive: true, newActive: false, hotActive: false});
        } else if (e.target.value === "New Arrivals") {
            setIsActive({bestActive: false, newActive: true, hotActive: false});
        } else if(e.target.value === "Hot Sales") {
            setIsActive({bestActive: false, newActive: false, hotActive: true});
        }
      };
    return (
            <section className="product spad">  
        <div className="container">
            <div className="row">
                <div className="col-lg-12">
                    <ul className="filter__controls">
                        <li className={isActive.bestActive ? "active" : ''} data-filter="*" onClick={handleClick}>Best Sellers</li>
                        <li className={isActive.newActive ? "active" : ''} data-filter=".new-arrivals">New Arrivals</li>
                        <li className={isActive.hotActive ? "active" : ''} data-filter=".hot-sales">Hot Sales</li>
                    </ul>
                </div>
            </div>
            <div className="row product__filter">
                {products.map(p => 
                < Product 
                    {...p}
                    key={p._id}
                />
                )}
            </div>
        </div>
    </section>
    );
}
